// 开始日期不大于结束时间，结束时间不小于开始时间，开始日期和结束日期都不大于当前日期。
function DatePicker(beginSelector,endSelector){
    // 仅选择日期
    $(beginSelector).datepicker(
        {
            language:  "zh-CN",
            autoclose: true,
            startView: 0,
            format: "yyyy-mm-dd",
            clearBtn:true,
            todayBtn:false,
            endDate:new Date()
        }).on('changeDate', function(ev){
        if(ev.date){
            $(endSelector).datepicker('setStartDate', new Date(ev.date.valueOf()))
        }else{
            $(endSelector).datepicker('setStartDate',);
        }
    })
    $(endSelector).datepicker(
        {
            language:  "zh-CN",
            autoclose: true,
            startView:0,
            format: "yyyy-mm-dd",
            clearBtn:true,
            todayBtn:false,
            endDate:new Date()
        }).on('changeDate', function(ev){
        if(ev.date){
            $(beginSelector).datepicker('setEndDate', new Date(ev.date.valueOf()))
        }else{
            $(beginSelector).datepicker('setEndDate',new Date());
        }
    })
}
$(function(){
    // select下拉框
    $(".dropdown-menu li a").on("click",function(){
        var dataValue = $(this).attr("data-value");
        var textValue = $(this).text();
        console.log(textValue);
        // 赋值
        $(this).parent().parent().siblings('.form_hidden_input').val(dataValue);
        $(this).parent().parent().siblings('.dropdown-toggle').children('.true_val').text(textValue);
    });

    DatePicker(".date_begin",".date_end");
    // 日期选择
    $(".select_date").datepicker({
        keyboardNavigation: false,
        forceParse: false,
        autoclose: true
    });
});